import { useState } from 'react';
import ConfidenceChart from './ConfidenceChart';
import ExportButton from './ExportButton';

export default function FraudDetectionSystem() {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  // Mock submit handler - replace with your API call
  const handleSubmit = async (formData) => {
    setLoading(true);
    try {
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1500));
      setResult({
        is_fraud: Math.random() > 0.5,
        confidence: Math.random().toFixed(2),
        transaction_id: `TX${Math.floor(Math.random() * 10000)}`,
        amount: formData.amount,
        reasons: [
          "Unusual transaction amount",
          "Frequent supplier changes"
        ]
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div id="report-container" className="max-w-4xl mx-auto p-6">
      <div className="bg-white rounded-xl shadow-md overflow-hidden p-6">
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Fraud Detection System</h1>
        
        {/* Form Section */}
        <form onSubmit={(e) => {
          e.preventDefault();
          handleSubmit({
            amount: e.target.elements.amount.value
          });
        }} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Amount ($)</label>
              <input
                name="amount"
                type="number"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            {/* Add more form fields as needed */}
          </div>
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-2 px-4 rounded-md text-white font-medium ${loading ? 'bg-blue-400' : 'bg-blue-600 hover:bg-blue-700'} transition-colors`}
          >
            {loading ? 'Analyzing...' : 'Check for Fraud'}
          </button>
        </form>

        {/* Results Section */}
        {result && (
          <div className="mt-8 border-t pt-6">
            <h2 className="text-xl font-bold text-gray-800 mb-4">Analysis Results</h2>
            
            <div className="flex flex-col md:flex-row gap-8">
              <div className="flex-1">
                <ConfidenceChart confidence={result.confidence * 100} />
              </div>
              
              <div className="flex-1 space-y-4">
                <div className={`p-4 rounded-lg ${result.is_fraud ? 'bg-red-50 border-l-4 border-red-500' : 'bg-green-50 border-l-4 border-green-500'}`}>
                  <h3 className="font-bold text-lg">
                    {result.is_fraud ? 'Fraud Detected' : 'Legitimate Transaction'}
                  </h3>
                  <p className="mt-1">
                    Confidence: <span className="font-semibold">{Math.round(result.confidence * 100)}%</span>
                  </p>
                </div>

                <div className="space-y-2">
                  <h4 className="font-semibold text-gray-700">Transaction Details</h4>
                  <div className="grid grid-cols-2 gap-2 text-sm">
                    <span className="text-gray-500">ID:</span>
                    <span>{result.transaction_id}</span>
                    <span className="text-gray-500">Amount:</span>
                    <span>${result.amount}</span>
                  </div>
                </div>

                {result.reasons?.length > 0 && (
                  <div>
                    <h4 className="font-semibold text-gray-700">Reasons</h4>
                    <ul className="list-disc pl-5 space-y-1 text-sm">
                      {result.reasons.map((reason, i) => (
                        <li key={i} className="text-gray-600">{reason}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>

            <ExportButton 
              data={{
                'Transaction ID': result.transaction_id,
                'Amount': `$${result.amount}`,
                'Status': result.is_fraud ? 'Fraud' : 'Legitimate',
                'Confidence': `${Math.round(result.confidence * 100)}%`,
                'Reasons': result.reasons?.join('; ') || ''
              }}
              elementId="report-container"
            />
          </div>
        )}
      </div>
    </div>
  );
}
