import { useState } from 'react';
import FraudDetectionSystem from './FraudDetectionSystem';

export default function HomePage() {
  const [showDetection, setShowDetection] = useState(false);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="bg-blue-600 text-white py-16 px-4 text-center">
        <h1 className="text-4xl font-bold mb-4">Fraud Detection System</h1>
        <p className="text-xl mb-8">Identify suspicious transactions in real-time</p>
        
        <button
          onClick={() => setShowDetection(!showDetection)}
          className="px-8 py-3 bg-white text-blue-600 rounded-lg font-bold
                   hover:bg-blue-50 transition-all shadow-lg"
        >
          {showDetection ? 'Hide Tool' : 'Start Detection'}
        </button>
      </div>

      {/* Main Content */}
      <div className="container mx-auto p-4">
        {showDetection ? (
          <div className="mt-8 bg-white rounded-xl shadow-md overflow-hidden p-6">
            <FraudDetectionSystem />
          </div>
        ) : (
          <div className="mt-12 grid md:grid-cols-3 gap-8">
            {/* Feature Cards */}
            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="font-bold text-lg mb-2">Real-time Analysis</h3>
              <p>Instant detection of fraudulent patterns in transactions</p>
            </div>
            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="font-bold text-lg mb-2">Export Reports</h3>
              <p>Generate PDF/CSV reports with detailed findings</p>
            </div>
            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="font-bold text-lg mb-2">Visual Dashboard</h3>
              <p>Interactive charts for confidence level visualization</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
