import { useState } from 'react';
import axios from 'axios';

export default function FraudDetectionSystem() {
  // Form state
  const [formData, setFormData] = useState({
    transactionId: '',
    amount: '',
    department: 'health',
    supplier: '',
    officerId: ''
  });

  // Results state
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Form handling
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'amount' ? parseFloat(value) || 0 : value
    }));
  };

  // API submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      const response = await axios.post('http://localhost:8000/api/detect-fraud', {
        transaction_id: formData.transactionId,
        amount: formData.amount,
        department: formData.department,
        supplier: formData.supplier,
        officer_id: formData.officerId
      });

      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md">
      <h1 className="text-2xl font-bold text-gray-800 mb-6">Fraud Detection System</h1>
      
      {/* Input Form */}
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Transaction ID
          </label>
          <input
            type="text"
            name="transactionId"
            value={formData.transactionId}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            required
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Amount ($)
          </label>
          <input
            type="number"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            min="0"
            step="0.01"
            required
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Department
          </label>
          <select
            name="department"
            value={formData.department}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
          >
            <option value="health">Health</option>
            <option value="education">Education</option>
            <option value="transport">Transport</option>
            <option value="defense">Defense</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Supplier
          </label>
          <input
            type="text"
            name="supplier"
            value={formData.supplier}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Officer ID
          </label>
          <input
            type="text"
            name="officerId"
            value={formData.officerId}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          className={`w-full py-2 px-4 rounded-md text-white font-medium ${
            loading ? 'bg-blue-400' : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          {loading ? 'Analyzing...' : 'Check for Fraud'}
        </button>
      </form>

      {/* Results Display */}
      {error && (
        <div className="mt-6 p-4 bg-red-50 text-red-700 rounded-md">
          <h3 className="font-bold">Error:</h3>
          <p>{error}</p>
        </div>
      )}

      {result && (
        <div className="mt-6 p-4 bg-gray-50 rounded-md">
          <h2 className="text-xl font-bold mb-3">Analysis Result</h2>
          <div className={`p-3 rounded-md ${
            result.is_fraud ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'
          }`}>
            <p className="font-semibold">
              {result.is_fraud ? 'ðŸš¨ Fraud Detected' : 'âœ… No Fraud Detected'}
            </p>
            <p>Confidence: {(result.confidence * 100).toFixed(1)}%</p>
            <p>Transaction ID: {result.transaction_id}</p>
            
            {result.reasons && (
              <div className="mt-2">
                <h4 className="font-medium">Reasons:</h4>
                <ul className="list-disc pl-5">
                  {result.reasons.map((reason, i) => (
                    <li key={i}>{reason}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
