import React, { useState } from 'react';
import axios from 'axios';

const DetectionForm = () => {
  const [formData, setFormData] = useState({
    transaction_id: '',
    department: '',
    contract_value: '',
    supplier: '',
    contract_duration: '',
    bidding_process: 'open',
    num_bidders: '',
    winning_bid_ratio: '',
    previous_contracts: '',
    officer_tenure: ''
  });
  
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:8000/api/detect', formData);
      setResult(response.data);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Fraud Detection Form</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Transaction ID</label>
            <input
              type="text"
              name="transaction_id"
              value={formData.transaction_id}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Department</label>
            <select
              name="department"
              value={formData.department}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            >
              <option value="">Select Department</option>
              <option value="health">Health</option>
              <option value="education">Education</option>
              <option value="transport">Transport</option>
              <option value="defense">Defense</option>
              <option value="infrastructure">Infrastructure</option>
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Contract Value ($)</label>
            <input
              type="number"
              name="contract_value"
              value={formData.contract_value}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Supplier</label>
            <input
              type="text"
              name="supplier"
              value={formData.supplier}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Contract Duration (months)</label>
            <input
              type="number"
              name="contract_duration"
              value={formData.contract_duration}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Bidding Process</label>
            <select
              name="bidding_process"
              value={formData.bidding_process}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            >
              <option value="open">Open</option>
              <option value="restricted">Restricted</option>
              <option value="direct">Direct</option>
              <option value="negotiated">Negotiated</option>
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Number of Bidders</label>
            <input
              type="number"
              name="num_bidders"
              value={formData.num_bidders}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Winning Bid Ratio</label>
            <input
              type="number"
              step="0.01"
              min="0"
              max="1"
              name="winning_bid_ratio"
              value={formData.winning_bid_ratio}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Previous Contracts with Supplier</label>
            <input
              type="number"
              name="previous_contracts"
              value={formData.previous_contracts}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700">Procurement Officer Tenure (years)</label>
            <input
              type="number"
              name="officer_tenure"
              value={formData.officer_tenure}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2 border"
              required
            />
          </div>
        </div>
        
        <button
          type="submit"
          disabled={loading}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline disabled:opacity-50"
        >
          {loading ? 'Analyzing...' : 'Detect Fraud'}
        </button>
      </form>
      
      {result && (
        <div className={`mt-6 p-4 rounded-md ${result.predicted_fraud ? 'bg-red-100 border-red-400 text-red-700' : 'bg-green-100 border-green-400 text-green-700'} border`}>
          <h3 className="font-bold text-lg mb-2">Detection Result</h3>
          <p>Transaction ID: {result.transaction_id}</p>
          <p>Prediction: {result.predicted_fraud ? 'Potential Fraud Detected' : 'No Fraud Detected'}</p>
          <p>Confidence: {(result.fraud_probability * 100).toFixed(2)}%</p>
          {result.predicted_fraud && (
            <p className="font-semibold mt-2">This transaction has been flagged for review!</p>
          )}
        </div>
      )}
    </div>
  );
};

export default DetectionForm;
